import { NativeModules } from 'react-native';

function init(appId) {
    NativeModules.BridgeModule.initialize(appId)
}
function updateFragmentManager() {
    NativeModules.BridgeModule.updateFragmentManager()
}
function reset() {
    NativeModules.BridgeModule.resetCampaignData()
}
function sendEvent(event) {
    NativeModules.BridgeModule.sendEvent(event)
}

module.exports = {
    init,
    updateFragmentManager,
    reset,
    sendEvent
}